const DIV_ID_LEFT_NAV="leftNav",DIV_ID_CORNER_NAV="cornerNav";window.onload=function(){highlightNavigation(),(document.URL.includes("index.html")||document.URL.includes("/search"))&&updateLinkShowAllPosts()};function updateLinkShowAllPosts(){Array.from(document.getElementsByTagName("a")).forEach(b=>{"Show all posts"==b.innerText&&(b.href=window.location.origin+"/index.html")})}function highlightNavigation(){var a=document.getElementById(DIV_ID_LEFT_NAV).getElementsByTagName("a"),b=document.getElementById(DIV_ID_CORNER_NAV).getElementsByTagName("a"),c=Array.from(a).concat(Array.from(b));c.forEach(a=>{document.URL.includes(a.href)&&(a.style.background="#e0e0e0")})}